<?php

require 'vendor/autoload.php';

use Firebase\JWT\JWT;
use Firebase\JWT\Key;

$PRIVATE_KEY = '';
    
$token = JWT::encode(
  [ 
    'sub' => '',
    'iat' => '',
  ],
  base64_decode($PRIVATE_KEY),
  'RS256'
);

header("Content-Type: application/json; charset=utf-8");
echo json_encode(["token" => $token]);

